// Get the string from the AI Agent output
const text = $json.output;

// Split into array
let parts = text.split(',').map(s => s.trim());

// First element = ATS score
let atsScore = parts.shift();

// The rest = keywords
let keywords = parts;

// Return as structured JSON for next nodes
return [
  {
    atsScore,
    keywords
  }
];
